#!/usr/bin/env node
import inquirer from "inquirer";

let answers = await inquirer.prompt([
    {
        name: "Fname",
        type: 'input',
        message: "Enter your first name: "
    },
    {
        name: "Lname",
        type: 'input',
        message: "Enter your last name: "
    },
    {
        name: "roll",
        type: "number | input",
        message: "Enter Roll No.: ",
    },
    {
        name: "email",
        type: "input | number",
        message: "Enter your email: ",
    },
    {
        name: "grade",
        type: "number",
        message: "Enter your grade: ",
        
    }
])



export {answers};